                merchandiseItems = merchandiseProvider.GetItems(imagesSourceDirectory.FullName);

            merchandiseItems.ProviderName = merchandiseProvider.Name;
        }

        forecasts = await ForecastService.GetForecastAsync(DateTime.Now);
    }

